import { useState } from 'react';
import { toast } from 'react-toastify';

export const StudentForm = ({ onCreate }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    course: '',
    age: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.course || !formData.age) {
      toast.error('All fields are required');
      return;
    }

    if (!formData.email.includes('@')) {
      toast.error('Please enter a valid email');
      return;
    }

    const success = await onCreate(formData);
    if (success) {
      setFormData({
        name: '',
        email: '',
        course: '',
        age: ''
      });
      toast.success('Student added successfully!');
    }
  };

  return (
    <div style={{
      maxWidth: '500px',
      margin: '0 auto',
      padding: '2rem',
      background: '#111',
      borderRadius: '10px',
      boxShadow: '0 2px 8px rgb(245, 242, 242)',
      color: 'black',
      fontFamily: 'Arial, sans-serif'
    }}>
      <h3 style={{ 
        marginBottom: '1.5rem',
        color: 'black',
        textAlign: 'center',
        fontSize: '1.5rem'
      }}>
        Add New Student
      </h3>
      <form onSubmit={handleSubmit}>
        {/* Name */}
        <div style={{ marginBottom: '1rem' }}>
          <label style={{ marginBottom: '0.5rem', display: 'block' }}>Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '1px solid #444',
              borderRadius: '6px',
              backgroundColor: '#222',
              color: 'black'
            }}
          />
        </div>

        {/* Email */}
        <div style={{ marginBottom: '1rem' }}>
          <label style={{ marginBottom: '0.5rem', display: 'block' }}>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '1px solid #444',
              borderRadius: '6px',
              backgroundColor: '#222',
              color: 'black'
            }}
          />
        </div>

        {/* Course Dropdown */}
        <div style={{ marginBottom: '1rem' }}>
          <label style={{ marginBottom: '0.5rem', display: 'block' }}>Course</label>
          <select
            name="course"
            value={formData.course}
            onChange={handleChange}
            required
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '1px solid #444',
              borderRadius: '6px',
              backgroundColor: '#222',
              color: 'white'
            }}
          >
            <option value="">Select a course</option>
            <option value="Computer Engineering">Computer Engineering</option>
            <option value="Mechanical Engineering">Mechanical Engineering</option>
            <option value="Electrical Engineering">Electrical Engineering</option>
            <option value="Civil Engineering">Civil Engineering</option>
            <option value="Electronics & Communication">Electronics & Communication</option>
            <option value="Chemical Engineering">Chemical Engineering</option>
          </select>
        </div>

        {/* Age */}
        <div style={{ marginBottom: '1.5rem' }}>
          <label style={{ marginBottom: '0.5rem', display: 'block' }}>Age</label>
          <input
            type="number"
            name="age"
            value={formData.age}
            onChange={handleChange}
            required
            min="16"
            style={{
              width: '100%',
              padding: '0.75rem',
              border: '1px solid #444',
              borderRadius: '6px',
              backgroundColor: '#222',
              color: 'white'
            }}
          />
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          style={{
            width: '100%',
            padding: '0.75rem',
            backgroundColor: 'white',
            color: 'black',
            fontWeight: 'bold',
            border: 'none',
            borderRadius: '6px',
            cursor: 'pointer',
            transition: 'all 0.3s ease'
          }}
        >
          Add Student
        </button>
      </form>
    </div>
  );
};
