import { auth } from "../firebase";
import { 
  signInWithEmailAndPassword, 
  signOut,
  onAuthStateChanged,
  GoogleAuthProvider,
  signInWithPopup
} from "firebase/auth";
import { toast } from "react-toastify";

// Email/Password Login
export const login = async (email, password) => {
  try {
    await signInWithEmailAndPassword(auth, email, password);
    toast.success("Login successful!");
    return true;
  } catch (error) {
    const errorMessage = getAuthErrorMessage(error.code);
    toast.error(errorMessage);
    console.error("Login error:", error);
    return false;
  }
};

// Google Sign-In
export const loginWithGoogle = async () => {
  try {
    const provider = new GoogleAuthProvider();
    await signInWithPopup(auth, provider);
    toast.success("Google login successful!");
    return true;
  } catch (error) {
    const errorMessage = getAuthErrorMessage(error.code);
    toast.error(errorMessage);
    console.error("Google sign-in error:", error);
    return false;
  }
};

// Logout
export const logout = async () => {
  try {
    await signOut(auth);
    toast.success("Logged out successfully");
  } catch (error) {
    toast.error("Logout failed");
    console.error("Logout error:", error);
  }
};

// Auth State Listener
export const onAuthChange = (callback) => {
  return onAuthStateChanged(auth, callback);
};

// Error Message Handler
const getAuthErrorMessage = (code) => {
  switch(code) {
    case "auth/invalid-email":
      return "Invalid email format";
    case "auth/user-disabled":
      return "Account disabled";
    case "auth/user-not-found":
      return "Email not found";
    case "auth/wrong-password":
      return "Incorrect password";
    case "auth/popup-closed-by-user":
      return "Google sign-in cancelled";
    case "auth/account-exists-with-different-credential":
      return "Account exists with different login method";
    case "auth/network-request-failed":
      return "Network error. Check your connection";
    case "auth/too-many-requests":
      return "Too many attempts. Try again later";
    default:
      return "Authentication failed. Please try again";
  }
};

// Optional: Test user auto-login in development
if (process.env.NODE_ENV === "development") {
  const testUsers = [
    { email: "admin@school.com", password: "Admin@123" },
    { email: "teacher@school.com", password: "Teach@123" }
  ];

  testUsers.forEach(async (user) => {
    try {
      await signInWithEmailAndPassword(auth, user.email, user.password);
    } catch (error) {
      console.log("Test user login skipped:", error.message);
    }
  });
}
