import { useState } from 'react';
import { toast } from 'react-toastify';

const inputContainerStyle = {
  position: 'relative',
  marginBottom: '1.5rem'
};

const labelStyle = (isFloating) => ({
  position: 'absolute',
  left: '0.75rem',
  top: isFloating ? '-0.75rem' : '0.75rem',
  fontSize: isFloating ? '0.8rem' : '1rem',
  color: 'white',
  backgroundColor: 'black',
  padding: '0 0.25rem',
  transition: 'all 0.2s ease-in-out',
  pointerEvents: 'none'
});

const inputStyle = {
  width: '100%',
  padding: '0.75rem',
  border: '1px solid white',
  borderRadius: '4px',
  backgroundColor: 'black',
  color: 'white',
  fontSize: '1rem'
};

export const StudentForm = ({ onCreate }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    course: '',
    age: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { name, email, course, age } = formData;

    if (!name || !email || !course || !age) {
      toast.error('All fields are required');
      return;
    }

    if (!email.includes('@')) {
      toast.error('Please enter a valid email');
      return;
    }

    const success = await onCreate(formData);
    if (success) {
      setFormData({ name: '', email: '', course: '', age: '' });
      toast.success('Student added successfully!');
    }
  };

  return (
    <div style={{
      maxWidth: '500px',
      margin: '0 auto',
      padding: '2rem',
      background: 'black',
      borderRadius: '8px',
      color: 'white'
    }}>
      <h3 style={{ textAlign: 'center', marginBottom: '2rem' }}>Add New Student</h3>
      <form onSubmit={handleSubmit}>
        {['name', 'email', 'course', 'age'].map((field) => (
          <div key={field} style={inputContainerStyle}>
            <label style={labelStyle(formData[field])}>
              {field.charAt(0).toUpperCase() + field.slice(1)}
            </label>
            {field === 'course' ? (
              <select
                name="course"
                value={formData.course}
                onChange={handleChange}
                style={inputStyle}
              >
                <option value="">Select a course</option>
                <option value="Computer Engineering">Computer Engineering</option>
                <option value="Electronics Engineering">Electronics Engineering</option>
                <option value="Mechanical Engineering">Mechanical Engineering</option>
                <option value="Civil Engineering">Civil Engineering</option>
                <option value="Information Technology">Information Technology</option>
              </select>
            ) : (
              <input
                type={field === 'age' ? 'number' : 'text'}
                name={field}
                value={formData[field]}
                onChange={handleChange}
                style={inputStyle}
              />
            )}
          </div>
        ))}
        <button type="submit" style={{
          backgroundColor: 'white',
          color: 'black',
          border: 'none',
          padding: '0.75rem',
          fontSize: '1rem',
          borderRadius: '4px',
          width: '100%',
          cursor: 'pointer'
        }}>
          Add Student
        </button>
      </form>
    </div>
  );
};
